jQuery(document).ready(function($){$(".term-color").wpColorPicker();var frame;$(".term-image-wrap .upload-img-button").on("click",function(event){event.preventDefault();if(frame){frame.open();return}frame=wp.media({library:{type:"image"},title:wp.media.view.l10n.chooseImage,multiple:false});frame.on("select",function(){var attachment=frame.state().get("selection").first().toJSON();$(".term-image-wrap .img-container").empty().append('<img src="'+attachment.url+'" alt="" style="max-width:100%;"/>');$(".term-image-wrap .img-id").val(attachment.id);$(".term-image-wrap .img-url").val(attachment.url);$(".term-image-wrap .delete-img-button").show()});frame.open()});$(".term-image-wrap .delete-img-button").on("click",function(event){event.preventDefault();$(".term-image-wrap .img-container").html("");$(this).hide();$(".term-image-wrap .img-id").val("");$(".term-image-wrap .img-url").val("")})});